</option><option value=\"crimson_editor\">Crimson Editor</option><option value=\"dawn\">Dawn</option><option value=\"dreamweaver\">Dreamweaver</option><option value=\"eclipse\">Eclipse</option><option value=\"github\">GitHub</option><option value=\"solarized_light\">Solarized Light</option><option value=\"textmate\">TextMate</option><option value=\"tomorrow\">Tomorrow</option><option value=\"xcode\">XCode</option></optgroup><optgroup label=\"Dark\"><option value=\"ambiance\">Ambiance</option><option value=\"chaos\">Chaos</option><option value=\"clouds_midnight\">Clouds Midnight</option><option value=\"cobalt\">Cobalt</option><option value=\"idle_fingers\">idleFingers</option><option value=\"kr_theme\">krTheme</option><option value=\"merbivore\">Merbivore</option><option value=\"merbivore_soft\">Merbivore Soft</option><option value=\"mono_industrial\">Mono Industrial</option><option value=\"monokai\">Monokai</option><option value=\"pastel_on_dark\">Pastel on dark</option><option value=\"solarized_dark\">Solarized Dark</option><option value=\"twilight\">Twilight</option><option value=\"tomorrow_night\">Tomorrow Night</option><option value=\"tomorrow_night_blue\">Tomorrow Night Blue</option><option value=\"tomorrow_night_bright\">Tomorrow Night Bright</option><option value=\"tomorrow_night_eighties\">Tomorrow Night 80s</option><option value=\"vibrant_ink\">Vibrant Ink</option></optgroup></select>&nbsp;&nbsp;\';\r\n			editorHtml +=\'</div>\';\r\n\r\n            $(\"#\"+editorId).after(editorHtml);\r\n            $(\"#\"+editorId).hide();\r\n            var _editor = ace.edit(\'editorPre_\'+editorId);\r\n            if(!mode)\r\n                mode = \"html\";\r\n            $(\"#ace_mode\"+editorId).val(mode);\r\n            _editor.getSession().setMode(\"ace/mode/\"+mode);\r\n            if(!theme)\r\n                theme=\"chrome\";\r\n            $(\"#theme\"+editorId).val(theme);\r\n            _editor.setTheme(\"ace/theme/\"+theme);\r\n            _editor.getSess