tring:function(){\r\n        return \'$!uploadServer/\';\r\n    },\r\n    get:function(urlStr){\r\n        return \'$!uploadServer/\' + urlStr;\r\n    }\r\n}; \r\n/**\r\n *使用方法:String.format(\"变量{0},占位{1}\", str1,str2)\r\n */\r\nString.format = function(src){\r\n    if (arguments.length == 0) return null;\r\n    var args = Array.prototype.slice.call(arguments, 1);\r\n    return src.replace(/\\{(\\d+)\\}/g, function(m, i){\r\n        return args[i];\r\n    });\r\n};\r\n \r\n/**\r\n *如果不是系统中配置的域名,就跳转过去\r\n */\r\nfunction checkDomain(){\r\n    var _urlTmp = window.location.href;\r\n     \r\n    if (_urlTmp.indexOf(\"$appServer\")==-1)\r\n    {\r\n        alert(\"本站必须使用域名$!{appServer}来访问,网页将自动跳转到首页.\");\r\n     \r\n        window.location=appServer.get(\"index\");\r\n    }\r\n}\r\n \r\n \r\nfunction frontGoToPage(pageNum,formID){\r\n    if(!pageNum)\r\n        pageNum = 1;\r\n    if(!formID)\r\n        formID = \'#pagerForm\';\r\n    $(formID).find(\"input[name=pageNum]\").val(pageNum);\r\n    $(formID).submit();\r\n}',99.00,'dbVMs','','ram','common',NULL,NULL,'','valid','textarea','R','','2013-02-21 23:54:18','2013-02-21 23:42:55');

